<template>
  <v-container>
    <v-stepper v-model="e1" flat elevation="0">
      <v-stepper-header>
        <v-stepper-step :complete="e1 > 1" step="1">
          type de services
        </v-stepper-step>

        <v-divider></v-divider>

        <v-stepper-step :complete="e1 > 2" step="2">Mes données </v-stepper-step>

        <v-divider></v-divider>

        <v-stepper-step :complete="e1 > 3" step="3">
          Choisir mon avatar
        </v-stepper-step>
        <v-divider></v-divider>

        <v-stepper-step step="4" :complete="e1 > 4">
          Mes Informations</v-stepper-step
        >
        <v-divider></v-divider>

        <v-stepper-step step="5"> Mot de passe </v-stepper-step>
      </v-stepper-header>
      <v-form ref="form">
        <v-stepper-items>
          <v-stepper-content step="1">
            <v-card class="mb-12" height="200px" elevation="0">
              <div class="text-center">
                <v-btn
                  rounded
                  class="ma-2"
                  outlined
                  color="primary"
                  @click="selectTypeService(2)"
                >
                  Je demande un service
                </v-btn>
                <v-btn
                  rounded
                  class="ma-2"
                  outlined
                  color="primary"
                  @click="selectTypeService(1)"
                >
                  J'offre mes services
                </v-btn>
              </div>
            </v-card>
          </v-stepper-content>

          <v-stepper-content step="2">
            <v-card class="mb-12" height="250px" elevation="0">
              <div class="text-left">
                <v-btn
                  class="ma-2"
                  v-bind:outlined="selectM"
                  color="primary"
                  rounded
                  @click="selectSexe(1)"
                >
                  M.
                </v-btn>
                <v-btn
                  rounded
                  class="ma-2"
                  v-bind:outlined="selectMme"
                  color="primary"
                  @click="selectSexe(2)"
                >
                  Mme.
                </v-btn>
                <v-text-field
                  v-model="form.lastName"
                  :rules="[rules.required]"
                  color="primary"
                  label="Nom"
                  required
                ></v-text-field>
                <v-text-field
                  v-model="form.firstName"
                  :rules="[rules.required]"
                  color="primary"
                  label="Prenom"
                  required
                ></v-text-field>
                <v-row>
                  <v-col cols="12">
                    <v-menu
                      ref="menu"
                      v-model="menu"
                      :close-on-content-click="false"
                      transition="scale-transition"
                      offset-y
                      min-width="auto"
                    >
                      <template v-slot:activator="{ on, attrs }">
                        <v-text-field
                          v-model="form.birthday"
                          label="Date de naissance"
                          prepend-icon="mdi-calendar"
                          readonly
                          v-bind="attrs"
                          v-on="on"
                          :rules="[rules.required]"
                        ></v-text-field>
                      </template>
                      <v-date-picker
                        v-model="form.birthday"
                        :active-picker.sync="activePicker"
                        :max="
                          new Date(
                            Date.now() - new Date().getTimezoneOffset() * 60000
                          )
                            .toISOString()
                            .substr(0, 10)
                        "
                        min="1950-01-01"
                        @change="save"
                      ></v-date-picker>
                    </v-menu>
                  </v-col>
                </v-row>
              </div>
            </v-card>

            <v-btn rounded color="primary" @click="continuer()">
              Continuer
            </v-btn>

            <v-btn rounded text @click="e1--"> Annuler </v-btn>
          </v-stepper-content>

          <v-stepper-content step="3">
            <v-card class="mb-12" height="320px" elevation="0">
              <p>Choisissez un avatar...</p>
              <v-avatar
                :key="index"
                v-for="(i, index) in avatars"
                size="90"
                class="ma-1"
              >
                <img :src="i.src" :alt="i.alt" @click="selectAvatar(index)" />
              </v-avatar>
            </v-card>

            <v-btn rounded text @click="e1--"> Annuler </v-btn>
          </v-stepper-content>

          <v-stepper-content step="4">
            <v-card class="mb-12" height="450px" elevation="0">
              <p>Informations personelles</p>
              <v-text-field
                v-model="form.email"
                :rules="emailRules"
                label="E-mail"
                required
              ></v-text-field>
              <v-text-field
                v-model="form.phone"
                :rules="[rules.required]"
                label="Numéro de téléphone"
                required
              ></v-text-field>
              <vuetify-google-autocomplete
                ref="t"
                id="map"
                classname="form-control"
                placeholder="addrese"
                v-on:placechanged="getAddressData"
                country="fr"
                label=""
                autocomplete="name"
                :rules="[rules.required]"
              >
              </vuetify-google-autocomplete>

              <v-text-field
                v-model="form.address"
                :rules="[rules.required]"
                label="Adresse"
                required
                readonly
              ></v-text-field>
              <v-text-field
                v-model="form.address2"
                :rules="[rules.required]"
                label="Complément d'adresse"
                required
                readonly
              ></v-text-field>
              <v-row>
                <v-col cols="6" sm="6" md="6">
                  <v-text-field
                    v-model="form.cp"
                    :rules="[rules.required]"
                    label="Code postal"
                    required
                    readonly
                  ></v-text-field>
                </v-col>
                <v-col cols="6" sm="6" md="6">
                  <v-text-field
                    v-model="form.city"
                    :rules="[rules.required]"
                    label="ville"
                    required
                    readonly
                  ></v-text-field>
                </v-col>
              </v-row>
            </v-card>

            <v-btn rounded color="primary" @click="continuer()">
              Continuer</v-btn
            >

            <v-btn rounded text @click="e1--"> Annuler </v-btn>
          </v-stepper-content>

          <v-stepper-content step="5">
            <v-card class="mb-12" height="200px" elevation="0">
              <p>Création de mot de passe</p>
              <v-text-field
                v-model="form.password"
                :append-icon="show1 ? 'mdi-eye' : 'mdi-eye-off'"
                :rules="[rules.required, rules.min]"
                :type="show1 ? 'text' : 'password'"
                name="input-10-1"
                label="Mot de passe"
                hint="At least 8 characters"
                counter
                @click:append="show1 = !show1"
              ></v-text-field>
            </v-card>

            <v-btn rounded color="primary" @click="signup()">
              S'inscrire
            </v-btn>
            <v-btn rounded text @click="e1--"> Annuler </v-btn>
          </v-stepper-content>
        </v-stepper-items>
      </v-form>
    </v-stepper>
    <v-alert type="error" id="alert-error" class="alert" v-model="error">
      une erreur s'est produite lors de la création de l'utilisateur
    </v-alert>
  </v-container>
</template>

<style scoped>
.v-stepper__header {
  box-shadow: none;
}
.alert {
  position: fixed;
  top: 10%;
  right: 5%;
  width: 90%;
}
</style>

<script>
import { mapActions, mapState } from "vuex";
import GoogleMapSearch from "../components/GoogleMapSearch.vue";

export default {
  components: { GoogleMapSearch },
  data: () => ({
    error: false,
    form: Object.assign({
      firstName: "Joseph",
      lastName: "Alonzo",
      birthday: null,
      terms: false,
      city: null,
      email: "josep_alonzo@icloud.com",
      phone: "0695126895",
      address: null,
      address2: null,
      cp: null,
      type: 1,
      sexe: 1,
      avatar: null,
      password: "qwertyuiop",
      addressGoogle: null,
      latitude : null,
      longitude: null,
      vigente: true
    }),
    valid: true,
    rules: {
      required: (value) => !!value || "Required.",
      min: (v) => v.length >= 8 || "Min 8 characters",
      emailMatch: () => `The email and password you entered don't match`,
    },
    // date: new Date(Date.now() - new Date().getTimezoneOffset() * 60000)
    //   .toISOString()
    //   .substr(0, 10),
    modal: false,
    emailRules: [
      (v) => !!v || "E-mail is required",
      (v) => /.+@.+\..+/.test(v) || "E-mail must be valid",
    ],
    e1: 1,
    selectM: false,
    selectMme: true,
    show1: false,
    avatars: [
      {
        src: "https://hirondelle-basket.fr/public/2768/upload/theme/profils/kal-theme-photo-profil-defaut-femme.jpg",
        alt: "1",
      },
      {
        src: "https://archi-africa.com/wp-content/uploads/2019/05/profil-avatar-femme-icone-ronde_24640-14042.jpg",
        alt: "1",
      },
      {
        src: "data:image/png;base64,",
        alt: "1",
      },
      {
        src: "data:image/png;base64,",
        alt: "1",
      },
      {
        src: "data:image/png;base64,",
        alt: "1",
      },
      {
        src: "data:image/png;base64,",
        alt: "1",
      },
      {
        src: "data:image/png;base64,",
        alt: "1",
      },
      {
        src: "data:image/png;base64,",
        alt: "1",
      },
      {
        src: "https://atformation.fr/wp-content/uploads/2021/09/at-formation-avis.png",
        alt: "1",
      },
    ],
    activePicker: null,
    date: null,
    menu: false,
  }),
  methods: {
    ...mapActions("user", ["add"]),
    save(date) {
      this.$refs.menu.save(date);
    },
    validate() {
      if (this.$refs.form.validate()) {
        this.$router.push("/SignupSenior");
      }
    },
    continuer() {
      let validate = false;
      switch (this.e1) {
        case 2:
          if (
            this.form.sexe &&
            this.form.firstName &&
            this.form.lastName &&
            this.form.birthday
          )
            validate = true;
          break;
        case 4:
          if (this.form.email && this.form.phone && this.form.addressGoogle)
            validate = true;
          break;
      }

      if (this.$refs.form.validate() || validate) {
        this.e1++;
      }
    },
    selectTypeService(type) {
      this.form.type = type;
      this.e1++;
    },
    selectSexe(sexe) {
      this.form.sexe = sexe;
      if (sexe == 1) {
        this.selectM = false;
        this.selectMme = true;
      } else {
        this.selectMme = false;
        this.selectM = true;
      }
    },

    getAddressData: function (addressData) {
      this.form.addressGoogle = addressData;
      this.form.latitude = addressData.latitude
      this.form.longitude = addressData.longitude
      this.form.address = addressData.street_number + " " + addressData.route;
      this.form.address2 = addressData.name;
      this.form.cp = addressData.postal_code;
      this.form.city = addressData.locality;
    },
    selectAvatar(index) {
      this.form.avatar = this.avatars[index].src;
      this.e1++;
    },
    ...mapActions('user', ['validateLogin']),
    async signup() {
      let data = await this.add(this.form);
      if (data['code']==200) {
        let user = await this.validateLogin({ email: this.form.email, password: this.form.password})
        if(user['code'] == 200 ){
          localStorage.address = this.form.address;
          localStorage.address2 = this.form.address2;
          localStorage.city = this.form.city;
          localStorage.cp = this.form.cp;
          localStorage.email = this.form.email;
          localStorage.firstName = this.form.firstName;
          localStorage.lastName = this.form.lastName;
          localStorage.id = user["data"][0].id;
          localStorage.phone = this.form.phone;
          localStorage.avatar = this.form.avatar;
          localStorage.sexe = this.form.sexe;
          localStorage.type = this.form.type;
          localStorage.rating = 0;
          localStorage.evaluations = 0;
        }
        this.$router.push("/");
      } else {
        this.error = true;
      }
    },
  },
  computed: {
    ...mapState("login", ["localId"]),
  },
  watch: {
    menu(val) {
      val && setTimeout(() => (this.activePicker = "YEAR"));
    },
  },
};
</script>